on.start = function () {
  state.groupBy = chix_group.groupBy.create()
  state.groupBy.on('group', function (group, by) {
    var $ = state.$
    var g = chix_group.send.create()
    output({xout: g.open()})
    output({out: g.write($.create(group)), by: $.create(by)})
    output({xout: g.close()})
  })
}


on.input.in = function () {
  state.groupBy.add($.get('in'))
}

on.input.by = function () {
  state.groupBy.setBy($.get('by'))
}

on.input.xin = function () {
  state.$ = $
  state.groupBy.receive($.get('xin'))
}

on.start = function start() {
  state.group = chix_group.recv.create()
  state.$ = null
  state.group.on('group', function groupHandler(group) {
    var p = state.$.create(group)
    p.removeMeta('chix-group')
    output({out: p})
  })
}

on.input.in = function () {
  state.group.add($.get('in'))
  state.$ = $
};

on.input.xin = function () {
  state.group.receive($.get('xin'))
  state.$ = $
};

on.start = function () {
  state.sync = chix_group.sync.create(['out1', 'out2'])
  state.sync.on('sync', function (itemId, set) {
    var $ = state.$
    var out = {}
    Object.keys(set).forEach(function (port) {
      out[port] = $.create(set[port])
    })
    output(out)
  })
}

on.input.in1 = function () {
  state.sync.add('out1', $.get('in1'))
}

on.input.in2 = function () {
  state.sync.add('out2', $.get('in2'))
}

on.input.xin = function () {
  state.$ = $
  state.sync.receive($.get('xin'))
}
